sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {

	},
	isDatePast : function(strDate) {
		// current Date Time
		var currentTime = new GlideDateTime();
		// user date inserted
		var testdate = new GlideDateTime(strDate);

		if (testdate.before(currentTime)) {
			return true;
		} 
		else {
			return false;
		}

	},
	isDateToday : function(strDate) {
		//current Date
		var currentDate = new GlideDateTime().getDate();
		var testDate = new GlideDateTime(strDate).getDate();

		if(currentDate.compareTo(testDate) == 0){
			return true;
		}
		else {
			return false;
		}
	},
	type: 'NeedItUtils'
};
